import React, { useEffect, useRef } from 'react';
import p5 from 'p5';
import { flockingSketch } from '../sketches/flockingSketch';

function Flocking({ params }) {
  const canvasRef = useRef();
  const p5Instance = useRef(null);
  const p5Ref = useRef(p5); // Store p5 library reference

  useEffect(() => {
    // Create new p5 instance and pass both p and the p5 library
    p5Instance.current = new p5(p => {
      // Create a modified sketch with p5 library access
      return flockingSketch(p, params, p5Ref.current);
    }, canvasRef.current);
    
    // Handle window resizing
    const handleResize = () => {
      if (p5Instance.current) {
        p5Instance.current.windowResized();
      }
    };
    
    window.addEventListener('resize', handleResize);
    
    // Cleanup function
    return () => {
      window.removeEventListener('resize', handleResize);
      if (p5Instance.current) {
        p5Instance.current.remove();
      }
    };
  }, [params]);

  // Update parameters when they change
  useEffect(() => {
    if (p5Instance.current) {
      p5Instance.current.updateParams(params);
    }
  }, [params]);

  return <div ref={canvasRef} className="canvas-container"></div>;
}

export default Flocking;
