import React from 'react';
import '../styles/Controls.css';

function Controls({ params, onParamChange, isPaused, onTogglePause, onDownloadSVG }) {
  const handleChange = (e) => {
    const { id, value } = e.target;
    onParamChange(id, parseFloat(value));
  };

  return (
    <div className="controls">
      <div className="control-group">
        <label htmlFor="separation">Separation</label>
        <input 
          type="range" 
          id="separation" 
          min="0" 
          max="3" 
          step="0.1" 
          value={params.separation} 
          onChange={handleChange} 
        />
      </div>
      <div className="control-group">
        <label htmlFor="alignment">Alignment</label>
        <input 
          type="range" 
          id="alignment" 
          min="0" 
          max="3" 
          step="0.1" 
          value={params.alignment} 
          onChange={handleChange} 
        />
      </div>
      <div className="control-group">
        <label htmlFor="cohesion">Cohesion</label>
        <input 
          type="range" 
          id="cohesion" 
          min="0" 
          max="3" 
          step="0.1" 
          value={params.cohesion} 
          onChange={handleChange} 
        />
      </div>
      <div className="control-group">
        <label htmlFor="newAgents">New Agents</label>
        <input 
          type="range" 
          id="newAgents" 
          min="1" 
          max="20" 
          step="1" 
          value={params.newAgents} 
          onChange={handleChange} 
        />
      </div>
      <div className="action-buttons">
        <button 
          className={`pause-button ${isPaused ? 'paused' : ''}`} 
          onClick={onTogglePause}
          title={isPaused ? "Resume" : "Pause"}
        >
          {isPaused ? "▶" : "❚❚"}
        </button>
        <button 
          className="download-button" 
          onClick={onDownloadSVG}
          title="Download PNG"
        >
          <svg viewBox="0 0 24 24" width="16" height="16">
            <path fill="white" d="M19,9h-4V3H9v6H5l7,7L19,9z M5,18v2h14v-2H5z"/>
          </svg>
        </button>
      </div>
    </div>
  );
}

export default Controls;
