import React, { useState } from 'react';
import Flocking from './components/Flocking';
import Controls from './components/Controls';
import DisplaySettings from './components/DisplaySettings';
import './styles/App.css';

function App() {
  const [params, setParams] = useState({
    separation: 1.5,
    alignment: 1.0,
    cohesion: 1.0,
    newAgents: 5
  });
  
  const [displaySettings, setDisplaySettings] = useState({
    trailLength: 200,
    trailThickness: 0.5,
    trailType: 'line',
    keepTrails: false,
    colorMode: 'single',
    agentShape: 'triangle',
    agentSize: 1,
    fadeAmount: 25,
    colors: {
      main: '#ffffff',
      start: '#ff0000',
      end: '#0000ff',
      agent: '#ffffff',
      background: '#000000'
    }
  });

  const [isPaused, setIsPaused] = useState(false);
  
  const handleParamChange = (param, value) => {
    setParams(prev => ({
      ...prev,
      [param]: value
    }));
  };
  
  const handleDisplaySettingsChange = (newSettings) => {
    setDisplaySettings(newSettings);
  };

  const togglePause = () => {
    setIsPaused(prev => !prev);
  };
  
  const downloadSVG = () => {
    const canvas = document.querySelector('.canvas-container canvas');
    if (!canvas) {
      console.error('Canvas element not found');
      return;
    }
    
    // Create a temporary anchor element
    const link = document.createElement('a');
    
    // Set the download name
    link.download = 'flocking-simulation.png';
    
    // Convert canvas to data URL
    try {
      link.href = canvas.toDataURL('image/png');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (e) {
      console.error('Error converting canvas to image', e);
    }
  };

  return (
    <div className="App">
      <div className="simulation-container">
        <Flocking 
          params={params} 
          displaySettings={displaySettings}
          isPaused={isPaused}
        />
        <Controls 
          params={params} 
          onParamChange={handleParamChange}
          isPaused={isPaused}
          onTogglePause={togglePause}
          onDownloadSVG={downloadSVG}
        />
        <DisplaySettings 
          settings={displaySettings} 
          onSettingsChange={handleDisplaySettingsChange} 
        />
      </div>
    </div>
  );
}

export default App;
